import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Страницы
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import CreateCourse from './pages/CreateCourse';
import Purchases from './pages/Purchases';
import Methodologies from './pages/Methodologies';
import CreateMethodology from './pages/CreateMethodology';
import ManageTemplates from './pages/ManageTemplates';
import AllUsers from './pages/AllUsers';

import { useUser } from './hooks/useUser';

function ProtectedRoute({ element: Element }) {
  const user = useUser();
  return user ? <Element /> : <Navigate to="/login" />;
}

function App() {
  return (
    <Router>
      <Routes>
        {/* Открытые маршруты */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        {/* Защищённые маршруты */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/create" element={<ProtectedRoute element={CreateCourse} />} />
        <Route path="/purchases" element={<ProtectedRoute element={Purchases} />} />
        <Route path="/methodologies" element={<ProtectedRoute element={Methodologies} />} />
        <Route path="/create-methodology" element={<ProtectedRoute element={CreateMethodology} />} />
        <Route path="/templates" element={<ProtectedRoute element={ManageTemplates} />} />
        <Route path="/users" element={<ProtectedRoute element={AllUsers} />} />

        <Route path="/" element={<Navigate to="/dashboard" />} />
      </Routes>
    </Router>
  );
}

export default App;
