import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function AllUsers() {
  const user = useUser();
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState(''); // Состояние для хранения значения поиска

  useEffect(() => {
    if (!user || user.role_name !== 'Руководитель') return;
    axios.get('/accounts').then(res => setUsers(res.data));
  }, [user]);

  // Фильтрация пользователей на основе введенного текста
  const filteredUsers = users.filter(user =>
    user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.role_name.toLowerCase().includes(searchTerm.toLowerCase()) // Можно фильтровать и по роли, если нужно
  );

  if (!user || user.role_name !== 'Руководитель') {
    return <p>Доступ запрещён</p>;
  }

  return (
    <div>
      <h2>Список всех пользователей</h2>
      
      {/* Поле поиска */}
      <input
        type="text"
        placeholder="Поиск по имени или роли"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)} // Обновляем значение поиска
        style={{ marginBottom: '20px', padding: '8px', width: '300px' }}
      />
      
      {/* Таблица пользователей */}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Имя пользователя</th>
            <th>Роль</th>
            <th>Дата регистрации</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length === 0 ? (
            <tr>
              <td colSpan="4">Пользователи не найдены</td>
            </tr>
          ) : (
            filteredUsers.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.role_name}</td>
                <td>{new Date(u.created_at).toLocaleDateString()}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AllUsers;
