import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function Purchases() {
  const user = useUser();
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    if (!user) return;
    axios.get('/purchases/my').then(res => setPurchases(res.data));
  }, [user]);

  const handleDownload = (purchaseId) => {
    // Запрос на скачивание файла
    axios.get(`/download/${purchaseId}`, { responseType: 'blob' })
      .then(response => {
        // Скачивание файла (сохранение)
        const file = new Blob([response.data]);
        const link = document.createElement('a');
        link.href = URL.createObjectURL(file);
        link.download = response.headers['content-disposition'].split('filename=')[1]; // Извлекаем имя файла
        link.click();
      })
      .catch(error => {
        console.error('Ошибка при скачивании файла:', error);
      });
  };
  

  return (
    <div>
      <h2>Мои покупки</h2>
      {purchases.length === 0 && <p>Нет купленных курсов</p>}
      <ul>
        {purchases.map(p => (
          <li key={p.id}>
            <b>{p.title}</b> — {p.is_downloaded ? '✅ Скачано' : '⏳ Не скачано'}
            {!p.is_downloaded && (
              <button onClick={() => handleDownload(p.course_material_id)} style={{ marginLeft: '10px' }}>
                Отметить скачивание
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Purchases;
