import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function AllUsers() {
  const user = useUser();
  const [users, setUsers] = useState([]);
  const [competencies, setCompetencies] = useState({});
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: 'id', direction: 'asc' });
  const [competencyInput, setCompetencyInput] = useState({});
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!user || user.role_name !== 'Руководитель') return;

    // Загружаем пользователей
    axios.get('/accounts')
      .then(res => setUsers(res.data))
      .catch(() => setError('Не удалось загрузить пользователей'));

    // Загружаем все компетенции
    axios.get('/competencies')
      .then(res => {
        const grouped = {};
        res.data.forEach(c => {
          if (!grouped[c.account_id]) grouped[c.account_id] = [];
          grouped[c.account_id].push(c.name);
        });
        setCompetencies(grouped);
      })
      .catch(() => setError('Не удалось загрузить компетенции'));
  }, [user]);

  const handleDelete = async (userId) => {
    if (window.confirm('Вы уверены, что хотите удалить этого пользователя?')) {
      try {
        await axios.delete(`/accounts/${userId}`);
        setUsers(users.filter(u => u.id !== userId));
        alert('Пользователь удалён успешно');
      } catch {
        alert('Ошибка при удалении пользователя');
      }
    }
  };

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') direction = 'desc';
    setSortConfig({ key, direction });
  };

  const sortedUsers = [...users].sort((a, b) => {
    if (a[sortConfig.key] < b[sortConfig.key]) return sortConfig.direction === 'asc' ? -1 : 1;
    if (a[sortConfig.key] > b[sortConfig.key]) return sortConfig.direction === 'asc' ? 1 : -1;
    return 0;
  });

  const filteredUsers = sortedUsers.filter(user =>
    user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.role_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    new Date(user.created_at).toLocaleDateString().includes(searchTerm)
  );

  const handleCompetencyChange = (userId, value) => {
    setCompetencyInput({ ...competencyInput, [userId]: value });
  };

  const handleAddCompetency = async (userId) => {
    const name = competencyInput[userId];
    if (!name) return;

    try {
      await axios.post('/accounts/competencies', { name, account_id: userId });
      setMessage(`Компетенция "${name}" добавлена пользователю ID ${userId}`);
      setCompetencyInput({ ...competencyInput, [userId]: '' });

      // Обновим список компетенций
      const res = await axios.get('/competencies');
      const grouped = {};
      res.data.forEach(c => {
        if (!grouped[c.account_id]) grouped[c.account_id] = [];
        grouped[c.account_id].push(c.name);
      });
      setCompetencies(grouped);
    } catch {
      setMessage('Ошибка при добавлении компетенции');
    }

    setTimeout(() => setMessage(''), 3000);
  };

  if (!user || user.role_name !== 'Руководитель') return <p>Доступ запрещён</p>;

  return (
    <div>
      <h2>Список всех пользователей</h2>

      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <input
        type="text"
        placeholder="Поиск по имени, роли или дате"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '20px', padding: '8px', width: '100%' }}
      />

      <table>
        <thead>
          <tr>
            <th><button onClick={() => handleSort('id')}>ID</button></th>
            <th><button onClick={() => handleSort('username')}>Имя</button></th>
            <th><button onClick={() => handleSort('role_name')}>Роль</button></th>
            <th><button onClick={() => handleSort('created_at')}>Дата регистрации</button></th>
            <th>Компетенции</th>
            <th>Добавить компетенцию</th>
            <th>Удаление</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length === 0 ? (
            <tr><td colSpan="7">Нет пользователей</td></tr>
          ) : (
            filteredUsers.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.role_name}</td>
                <td>{new Date(u.created_at).toLocaleDateString()}</td>
                <td>
                  {(competencies[u.id] || []).length > 0
                    ? competencies[u.id].join(', ')
                    : <i>Нет</i>}
                </td>
                <td>
                  <input
                    type="text"
                    placeholder="Компетенция"
                    value={competencyInput[u.id] || ''}
                    onChange={(e) => handleCompetencyChange(u.id, e.target.value)}
                  />
                  <button onClick={() => handleAddCompetency(u.id)}>Добавить</button>
                </td>
                <td>
                  <button onClick={() => handleDelete(u.id)} style={{ color: 'red' }}>Удалить</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AllUsers;
