import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from '../hooks/useUser';
import { useNavigate } from 'react-router-dom';
import axios from '../api/axiosInstance';

function Profile() {
  const user = useUser();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleWithdraw = async () => {
    try {
      await axios.post('/withdraw');
      alert('Заявка на вывод средств отправлена!');
    } catch (err) {
      alert('Ошибка при выводе средств');
    }
  };

  return (
    <div>
      <h2>Личный кабинет</h2>
      {user && <p>Здравствуйте, {user.username} ({user.role_name})</p>}

      {user?.role_name === 'Руководитель' && (
        <Link to="/create"><button>Создать курс</button></Link>
      )}
      <Link to="/methodologies"><button>Методологии</button></Link>
      {['Учитель', 'Руководитель'].includes(user?.role_name) && (
        <Link to="/create-methodology"><button>Создать методологию</button></Link>
      )}
      {user?.role_name === 'Руководитель' && (
        <Link to="/templates"><button>Управление шаблонами</button></Link>
      )}
      {user?.role_name === 'Руководитель' && (
        <Link to="/users"><button>Список пользователей</button></Link>
      )}
      {user?.role_name === 'Амбассадор' && (
        <Link to="/create"><button>Загрузить новый курс</button></Link>
      )}
      {user?.role_name === 'Амбассадор' && (
        <Link to="/sales-stats"><button>Статистика продаж</button></Link>
      )}
      {user?.role_name === 'Амбассадор' && (
        <button onClick={handleWithdraw}>Вывести средства</button>
      )}
      <button onClick={handleLogout}>Выход</button>
    </div>
  );
}

export default Profile;
