import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function Purchases() {
  const user = useUser();
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    if (!user) return;
    axios.get('/purchases/my').then(res => setPurchases(res.data));
  }, [user]);

  const handleDownload = async (course_material_id) => {
    try {
      await axios.post('/purchases/download', { course_material_id });
      alert('Скачивание отмечено!');
    } catch {
      alert('Ошибка при скачивании');
    }
  };

  return (
    <div>
      <h2>Мои покупки</h2>
      {purchases.length === 0 && <p>Нет купленных курсов</p>}
      <ul>
        {purchases.map(p => (
          <li key={p.id}>
            <b>{p.title}</b> — {p.is_downloaded ? '✅ Скачано' : '⏳ Не скачано'}
            {!p.is_downloaded && (
              <button onClick={() => handleDownload(p.course_material_id)} style={{ marginLeft: '10px' }}>
                Отметить скачивание
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Purchases;
