import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';

// Страницы
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Catalog from './pages/Catalog';
import Purchases from './pages/Purchases';
import Methodologies from './pages/Methodologies';
import CreateMethodology from './pages/CreateMethodology';
import ManageTemplates from './pages/ManageTemplates';
import AllUsers from './pages/AllUsers';

import Header from './components/Header'; // Импортируем Header
import { useUser } from './hooks/useUser';

function App() {
  const location = useLocation();
  const user = useUser();

  // Проверяем, находимся ли мы на странице логина или регистрации
  const hideHeader = location.pathname === '/login' || location.pathname === '/register';

  return (
    <Router>
      {!hideHeader && <Header />} {/* Показываем Header только на нужных страницах */}
      <Routes>
        {/* Открытые маршруты */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Защищённые маршруты */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/catalog" element={<Catalog />} />
        <Route path="/purchases" element={<Purchases />} />
        <Route path="/methodologies" element={<Methodologies />} />
        <Route path="/create-methodology" element={<CreateMethodology />} />
        <Route path="/templates" element={<ManageTemplates />} />
        <Route path="/users" element={<AllUsers />} />

        <Route path="/" element={<Navigate to="/dashboard" />} />
      </Routes>
    </Router>
  );
}

export default App;
