import React, { useEffect, useState } from 'react';
import { useUser } from '../hooks/useUser';  // Хук для получения данных о текущем пользователе
import axios from '../api/axiosInstance';  // Axios для запросов к серверу
import { Link } from 'react-router-dom';

function Profile() {
  const user = useUser();  // Получаем информацию о текущем пользователе
  const [purchasedCourses, setPurchasedCourses] = useState([]);  // Список купленных курсов
  const [createdCourses, setCreatedCourses] = useState([]);  // Список курсов, созданных пользователем
  const [loading, setLoading] = useState(true);  // Состояние загрузки

  useEffect(() => {
    if (!user) return;

    // Для учеников и учителей — получаем купленные курсы
    if (user?.role_name === 'Учитель' || user?.role_name === 'Ученик') {
      axios.get('/purchases/my-courses')
        .then((res) => {
          setPurchasedCourses(res.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Ошибка при получении купленных курсов:", error);
          setLoading(false);
        });
    }

    // Для руководителей и амбассадоров — получаем курсы, созданные пользователем
    if (user?.role_name === 'Руководитель' || user?.role_name === 'Амбассадор') {
      axios.get(`/courses/created-by/${user?.id}`)
        .then((res) => {
          setCreatedCourses(res.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Ошибка при получении созданных курсов:", error);
          setLoading(false);
        });
    }
  }, [user]);  // Хук будет срабатывать при изменении данных о пользователе

  const handleLogout = () => {
    localStorage.removeItem('token');  // Удаляем токен при выходе
    window.location.href = '/login';  // Перенаправляем на страницу логина
  };

  if (loading) {
    return <div>Загрузка...</div>;  // Пока загружаются данные, отображаем индикатор загрузки
  }

  return (
    <div>
      <h2>Личный кабинет</h2>
      {user && <p>Здравствуйте, {user.username} ({user.role_name})</p>}

      {/* Если ученик или учитель, показываем список купленных курсов */}
      {(user?.role_name === 'Учитель' || user?.role_name === 'Ученик') && (
        <div>
          <h3>Мои курсы</h3>
          {purchasedCourses.length === 0 ? (
            <p>У вас нет купленных курсов.</p>
          ) : (
            <ul>
              {purchasedCourses.map((course) => (
                <li key={course.id}>
                  <b>{course.title}</b> — {course.price}₽
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Если руководитель или амбассадор, показываем список их курсов */}
      {(user?.role_name === 'Руководитель' || user?.role_name === 'Амбассадор') && (
        <div>
          <h3>Мои курсы</h3>
          {createdCourses.length === 0 ? (
            <p>Вы еще не создали курсы.</p>
          ) : (
            <ul>
              {createdCourses.map((course) => (
                <li key={course.id}>
                  <b>{course.title}</b> — {course.price}₽
                  <Link to={`/courses/${course.id}`}>Подробнее</Link>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}

export default Profile;
