import React, { useState } from 'react';
import axios from '../api/axiosInstance';
import { useNavigate, Link } from 'react-router-dom';
import LoadingSpinner from './LoadingSpinner';  // Импортируем компонент загрузки

function Login() {
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);  // Состояние для индикатора загрузки
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);  // Включаем индикатор загрузки
    setError('');

    if (!form.username || !form.password) {
      setError('Пожалуйста, заполните все поля!');
      setLoading(false);  // Отключаем индикатор
      return;
    }

    try {
      const res = await axios.post('/auth/login', form);
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError('Неверные данные для входа. Проверьте имя пользователя и пароль.');
      setLoading(false);  // Отключаем индикатор
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Вход</h2>
      <input
        name="username"
        onChange={handleChange}
        placeholder="Имя пользователя"
        required
      />
      <input
        type="password"
        name="password"
        onChange={handleChange}
        placeholder="Пароль"
        required
      />
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Сообщение об ошибке */}
      
      {loading ? (  // Если идет загрузка, показываем спиннер
        <LoadingSpinner />
      ) : (
        <button type="submit">Войти</button>
      )}

      <p>Нет аккаунта? <Link to="/register">Зарегистрироваться</Link></p>
    </form>
  );
}

export default Login;
