// frontend/Purchases.js
import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function Purchases() {
  const user = useUser();
  const [purchases, setPurchases] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!user) return;
    axios.get('/purchases/my').then(res => setPurchases(res.data));
  }, [user]);

  const handleDownload = async (courseMaterialId) => {
    try {
      const response = await axios.get(`/download/${courseMaterialId}`, { responseType: 'blob' });

      // Создаем ссылку для скачивания
      const file = new Blob([response.data], { type: response.headers['content-type'] });
      const link = document.createElement('a');

      // Получаем имя файла из заголовков (если оно задано)
      const filename = response.headers['content-disposition']
        ? response.headers['content-disposition'].split('filename=')[1]
        : 'downloaded-file';

      // Скачиваем файл
      link.href = URL.createObjectURL(file);
      link.download = filename;
      link.click();
    } catch (err) {
      console.error('Ошибка при скачивании файла:', err);
      setError('Ошибка при скачивании файла');
    }
  };

  return (
    <div>
      <h2>Мои покупки</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {purchases.length === 0 && <p>Нет купленных курсов</p>}
      <ul>
        {purchases.map((purchase) => (
          <li key={purchase.id}>
            <b>{purchase.title}</b> — {purchase.is_downloaded ? '✅ Скачано' : '⏳ Не скачано'}
            {!purchase.is_downloaded && (
              <button onClick={() => handleDownload(purchase.course_material_id)}>
                Скачать
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Purchases;
