import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function Dashboard() {
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/materials').then((res) => {
      setMaterials(res.data);
      setLoading(false);
    });
  }, []);

  const handleBuy = async (material_id) => {
    try {
      await axios.post('/purchases/buy', { course_material_id: material_id });
      alert('Курс куплен!');
    } catch (err) {
      alert('Вы уже приобрели этот курс или произошла ошибка.');
    }
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div>
      <h3>Доступные курсы:</h3>
      <ul>
        {materials.map(mat => (
          <li key={mat.id}>
            <b>{mat.title}</b> — {mat.price}₽
            <button onClick={() => handleBuy(mat.id)} style={{ marginLeft: '10px' }}>
              Купить
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
