import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance';

function Catalog() {
  const [courses, setCourses] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filters, setFilters] = useState({
    category: '',
    author: '',
    minPrice: 0,
    maxPrice: 1000
  });

  useEffect(() => {
    // Загрузить курсы
    axios.get('/materials').then(res => setCourses(res.data));

    // Загрузить категории (если они есть)
    axios.get('/categories').then(res => setCategories(res.data));
  }, []);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const filteredCourses = courses.filter(course => {
    return (
      (filters.category ? course.category === filters.category : true) &&
      (filters.author ? course.author === filters.author : true) &&
      (course.price >= filters.minPrice && course.price <= filters.maxPrice)
    );
  });

  return (
    <div>
      <h2>Каталог курсов</h2>

      <div>
        <label>Категория:</label>
        <select name="category" onChange={handleFilterChange}>
          <option value="">Все</option>
          {categories.map(c => <option key={c.id} value={c.name}>{c.name}</option>)}
        </select>
      </div>

      <div>
        <label>Автор:</label>
        <input type="text" name="author" onChange={handleFilterChange} placeholder="Поиск по автору" />
      </div>

      <div>
        <label>Цена:</label>
        <input type="number" name="minPrice" value={filters.minPrice} onChange={handleFilterChange} placeholder="От" />
        <input type="number" name="maxPrice" value={filters.maxPrice} onChange={handleFilterChange} placeholder="До" />
      </div>

      <div>
        <ul>
          {filteredCourses.map(course => (
            <li key={course.id}>
              {course.title} — {course.price}₽
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Catalog;
