import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function Purchases() {
  const user = useUser();
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    if (!user) return;
    axios.get('/purchases/my').then(res => setPurchases(res.data));
  }, [user]);

  const handleDownload = async (course_material_id) => {
    try {
      // Сначала отправим запрос на сервер для отметки скачивания
      await axios.get('/purchases/download', { params: { course_material_id } });
  
      // Затем запросим сам файл для скачивания
      const response = await axios({
        url: '/purchases/download', // Путь для скачивания
        method: 'GET', // Используем GET для скачивания
        responseType: 'blob', // Ожидаем ответ в виде файла (blob)
        params: { course_material_id },  // Передаем ID материала
      });
  
      // Создаем ссылку для скачивания файла
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'downloaded_course'); // Имя файла для скачивания
      document.body.appendChild(link);
      link.click();
  
      alert('Скачивание завершено!');
    } catch (err) {
      alert('Ошибка при скачивании');
      console.error('Ошибка при скачивании:', err);
    }
  };
  

  return (
    <div>
      <h2>Мои покупки</h2>
      {purchases.length === 0 && <p>Нет купленных курсов</p>}
      <ul>
        {purchases.map(p => (
          <li key={p.id}>
            <b>{p.title}</b> — {p.is_downloaded ? '✅ Скачано' : '⏳ Не скачано'}
            {!p.is_downloaded && (
              <button onClick={() => handleDownload(p.course_material_id)} style={{ marginLeft: '10px' }}>
                Отметить скачивание
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Purchases;
