import React from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { useLoading } from './context/LoadingContext'; // Импортируем useLoading
import LoadingSpinner from './components/LoadingSpinner';  // Импортируем LoadingSpinner

// Страницы
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Catalog from './pages/Catalog';
import Purchases from './pages/Purchases';
import Methodologies from './pages/Methodologies';
import CreateMethodology from './pages/CreateMethodology';
import ManageTemplates from './pages/ManageTemplates';
import AllUsers from './pages/AllUsers';

import Header from './components/Header'; // Импортируем Header

function App() {
  const location = useLocation(); // Получаем текущий путь
  const hideHeader = location.pathname === '/login' || location.pathname === '/register'; // Скрытие Header
  const { loading } = useLoading();

  return (
    <div>
      {loading && <LoadingSpinner />}
      {/* Показываем Header только на нужных страницах */}
      {!hideHeader && <Header />} 

      <Routes>
        {/* Открытые маршруты */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Защищённые маршруты */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/catalog" element={<Catalog />} />
        <Route path="/purchases" element={<Purchases />} />
        <Route path="/methodologies" element={<Methodologies />} />
        <Route path="/create-methodology" element={<CreateMethodology />} />
        <Route path="/templates" element={<ManageTemplates />} />
        <Route path="/users" element={<AllUsers />} />

        <Route path="/" element={<Navigate to="/dashboard" />} />
      </Routes>
    </div>
  );
}

export default App;
