import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';
import { useNavigate } from 'react-router-dom';

function CreateCourse() {
  const user = useUser();
  const navigate = useNavigate();
  const [templates, setTemplates] = useState([]);
  const [form, setForm] = useState({
    template_id: '',
    title: '',
    description: '',
    price: '',
    is_available: true,
  });

  useEffect(() => {
    if (!user) return;
    axios.get('/templates').then(res => setTemplates(res.data));
  }, [user]);

  if (!user || user.role_name !== 'Руководитель') {
    return <p>Доступ запрещён</p>;
  }

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === 'checkbox' ? checked : value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/materials', form);
      alert('Курс создан!');
      navigate('/dashboard');
    } catch (err) {
      alert('Ошибка при создании курса');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Создание курса</h2>

      <select name="template_id" onChange={handleChange} required>
        <option value="">Выберите шаблон</option>
        {templates.map(t => (
          <option key={t.id} value={t.id}>{t.name}</option>
        ))}
      </select>

      <input name="title" placeholder="Название курса" onChange={handleChange} required />
      <textarea name="description" placeholder="Описание" onChange={handleChange} />
      <input name="price" type="number" placeholder="Цена" onChange={handleChange} required />

      <label>
        <input type="checkbox" name="is_available" checked={form.is_available} onChange={handleChange} />
        Курс доступен для покупки
      </label>

      <button type="submit">Создать курс</button>
    </form>
  );
}

export default CreateCourse;
