import React, { useState, useEffect } from 'react';
import { useUser } from '../hooks/useUser';  
import axios from '../api/axiosInstance';  
import { Link } from 'react-router-dom';

function Profile() {
  const user = useUser(); 
  const [purchasedCourses, setPurchasedCourses] = useState([]);  
  const [createdCourses, setCreatedCourses] = useState([]);  
  const [loading, setLoading] = useState(true);  
  const [editProfile, setEditProfile] = useState(false);  // Состояние для редактирования профиля
  const [newUsername, setNewUsername] = useState(user?.username || '');  // Новое имя пользователя
  const [newPassword, setNewPassword] = useState('');  // Новый пароль

  useEffect(() => {
    if (!user) return;  // Выход, если нет данных о пользователе
    setLoading(true);  // Начинаем загрузку

    // Запрос для покупок пользователя (для всех ролей, включая Амбассадора)
    axios.get('/purchases/my')
      .then((res) => {
        setPurchasedCourses(res.data);
      })
      .catch((error) => {
        console.error("Ошибка при получении купленных курсов:", error);
      });

    // Запрос для созданных курсов (только для Руководителей и Амбассадоров)
    if (user?.role_name === 'Руководитель' || user?.role_name === 'Амбассадор') {
      axios.get(`/purchases/created-by/${user?.id}`)
        .then((res) => {
          console.log('Полученные курсы для амбассадора:', res.data);
          setCreatedCourses(res.data);
        })
        .catch((error) => {
          console.error("Ошибка при получении созданных курсов:", error);
        });
    }

    // Завершаем загрузку
    setLoading(false);
  }, [user]);  

  const handleDelete = async (courseId) => {
    if (window.confirm('Вы уверены, что хотите удалить этот курс?')) {
      try {
        // Запрос на удаление курса
        await axios.delete(`/materials/${courseId}`);
        
        // Убираем удалённый курс из списка
        setCreatedCourses(createdCourses.filter(course => course.id !== courseId));

        alert('Курс успешно удалён');
      } catch (error) {
        console.error('Ошибка при удалении курса:', error);
        alert('Ошибка при удалении курса');
      }
    }
  };

  const handleProfileEdit = async (e) => {
    e.preventDefault();
    try {
      const data = {};
      if (newUsername !== user.username) data.username = newUsername;
      if (newPassword) data.password = newPassword;

      const response = await axios.patch('/accounts/profile', data);  // Предположим, что у нас есть endpoint для изменения профиля
      alert('Профиль успешно обновлён');
      setEditProfile(false);  // Закрыть форму редактирования
    } catch (error) {
      alert('Ошибка при обновлении профиля');
      console.error("Ошибка при редактировании профиля:", error);
    }
  };

  if (loading) {
    return <div>Загрузка...</div>; 
  }

  return (
    <div>
      <h2>Личный кабинет</h2>
      {user && <p>Здравствуйте, {user.username} ({user.role_name})</p>}

      {/* Редактирование профиля */}
      {editProfile ? (
        <div>
          <h3>Редактировать профиль</h3>
          <form onSubmit={handleProfileEdit}>
            <div>
              <label>Имя:</label>
              <input
                type="text"
                value={newUsername}
                onChange={(e) => setNewUsername(e.target.value)}
              />
            </div>
            <div>
              <label>Пароль:</label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
            </div>
            <button type="submit">Сохранить изменения</button>
            <button type="button" onClick={() => setEditProfile(false)}>
              Отмена
            </button>
          </form>
        </div>
      ) : (
        <button onClick={() => setEditProfile(true)}>Редактировать профиль</button>
      )}

      {/* Раздел для учеников и учителей */}
      {(user?.role_name === 'Учитель' || user?.role_name === 'Ученик') && (
        <div>
          <h3>Мои курсы</h3>
          {purchasedCourses.length === 0 ? (
            <p>У вас нет купленных курсов.</p>
          ) : (
            <ul>
              {purchasedCourses.map((course) => (
                <li key={course.id}>
                  <b>{course.title}</b> — {course.price}₽
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Раздел для руководителей и амбассадоров */}
      {(user?.role_name === 'Руководитель' || user?.role_name === 'Амбассадор') && (
        <div>
          <h3>Мои купленные курсы</h3>
          {purchasedCourses.length === 0 ? (
            <p>У вас нет купленных курсов.</p>
          ) : (
            <ul>
              {purchasedCourses.map((course) => (
                <li key={course.id}>
                  <b>{course.title}</b> — {course.price}₽
                </li>
              ))}
            </ul>
          )}

          <h3>Мои созданные курсы</h3>
          {createdCourses.length === 0 ? (
            <p>Вы еще не создали курсы.</p>
          ) : (
            <ul>
              {createdCourses.map((course) => (
                <li key={course.id}>
                  <b>{course.title}</b> — {course.price}₽
                  <Link to={`/courses/${course.id}`}>Подробнее</Link>
                  <button onClick={() => handleDelete(course.id)} style={{ marginLeft: '10px', color: 'red' }}>
                    Удалить
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}

export default Profile;
