import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';  // Путь к вашему axios-инстансу
import { useUser } from '../hooks/useUser';

function AllUsers() {
  const user = useUser();
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState(''); // Состояние для поиска
  const [sortConfig, setSortConfig] = useState({ key: 'id', direction: 'asc' }); // Состояние для сортировки

  // Загружаем список пользователей
  useEffect(() => {
    if (!user || user.role_name !== 'Руководитель') return;
    axios.get('/accounts')
      .then(res => setUsers(res.data))
      .catch(err => setError('Не удалось загрузить пользователей'));
  }, [user]);

  const handleDelete = async (userId) => {
    if (window.confirm('Вы уверены, что хотите удалить этого пользователя?')) {
      try {
        // Запрос на удаление пользователя
        await axios.delete(`/accounts/${userId}`);
        
        // Убираем удалённого пользователя из списка
        setUsers(users.filter(u => u.id !== userId));

        alert('Пользователь удалён успешно');
      } catch (err) {
        console.error('Ошибка при удалении пользователя:', err);
        alert('Ошибка при удалении пользователя');
      }
    }
  };

  // Функция для обработки сортировки
  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc'; // Меняем направление сортировки
    }
    setSortConfig({ key, direction });
  };

  // Функция сортировки
  const sortedUsers = [...users].sort((a, b) => {
    if (a[sortConfig.key] < b[sortConfig.key]) {
      return sortConfig.direction === 'asc' ? -1 : 1;
    }
    if (a[sortConfig.key] > b[sortConfig.key]) {
      return sortConfig.direction === 'asc' ? 1 : -1;
    }
    return 0;
  });

  // Фильтрация по поисковому запросу
  const filteredUsers = sortedUsers.filter(user => {
    return (
      user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.role_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      new Date(user.created_at).toLocaleDateString().includes(searchTerm)
    );
  });

  if (!user || user.role_name !== 'Руководитель') {
    return <p>Доступ запрещён</p>;
  }

  return (
    <div>
      <h2>Список всех пользователей</h2>

      {/* Поле для поиска */}
      <input
        type="text"
        placeholder="Поиск по имени, роли или дате"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '20px', padding: '8px', width: '100%' }}
      />

      {/* Отображение ошибки, если не удалось загрузить пользователей */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table>
        <thead>
          <tr>
            <th>
              <button onClick={() => handleSort('id')}>
                ID {sortConfig.key === 'id' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
              </button>
            </th>
            <th>
              <button onClick={() => handleSort('username')}>
                Имя пользователя {sortConfig.key === 'username' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
              </button>
            </th>
            <th>
              <button onClick={() => handleSort('role_name')}>
                Роль {sortConfig.key === 'role_name' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
              </button>
            </th>
            <th>
              <button onClick={() => handleSort('created_at')}>
                Дата регистрации {sortConfig.key === 'created_at' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
              </button>
            </th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length === 0 ? (
            <tr>
              <td colSpan="5">Нет пользователей для отображения</td>
            </tr>
          ) : (
            filteredUsers.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.username}</td>
                <td>{u.role_name}</td>
                <td>{new Date(u.created_at).toLocaleDateString()}</td>
                <td>
                  <button onClick={() => handleDelete(u.id)} style={{ color: 'red' }}>
                    Удалить
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AllUsers;
