import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';
import { Link } from 'react-router-dom';

function Dashboard() {
    const navigate = useNavigate();
  const user = useUser();
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    if (!user) return;
    axios.get('/materials').then((res) => setMaterials(res.data));
  }, [user]);

  const handleBuy = async (material_id) => {
    try {
      await axios.post('/purchases/buy', { course_material_id: material_id });
      alert('Курс куплен!');
    } catch (err) {
      alert('Вы уже приобрели этот курс или произошла ошибка.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };
  return (
    <div>
      <h2>Личный кабинет</h2>
      {user && <p>Здравствуйте, {user.username} ({user.role_name})</p>}

      {user?.role_name === 'Руководитель' && (
        <Link to="/create"><button>Создать курс</button></Link>
      )}
        <Link to="/methodologies"><button>Методологии</button></Link>
        {['Учитель', 'Руководитель'].includes(user?.role_name) && (
  <Link to="/create-methodology"><button>Создать методологию</button></Link>
)}
    {user?.role_name === 'Руководитель' && (
  <Link to="/templates"><button>Управление шаблонами</button></Link>
)}
{user?.role_name === 'Руководитель' && (
  <Link to="/users"><button>Список пользователей</button></Link>
)}
      <h3>Доступные курсы:</h3>
      <ul>
        {materials.map(mat => (
          <li key={mat.id}>
            <b>{mat.title}</b> — {mat.price}₽
            <button onClick={() => handleBuy(mat.id)} style={{ marginLeft: '10px' }}>
              Купить
            </button>
          </li>
        ))}
      </ul>

      <Link to="/purchases"><button>Мои покупки</button></Link>
      <button onClick={handleLogout}>Выход</button>
    </div>
  );
}

export default Dashboard;
