import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance';  // Путь к вашему axios-инстансу
import { useNavigate } from 'react-router-dom';
import { useUser } from '../hooks/useUser'; 

function CreateCourse() {
    const user = useUser();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: '',
    description: '',
    price: '',
    file: null,
    template_id: '', // Новый параметр для выбора шаблона
    created_at: new Date().toISOString(), // Добавляем текущую дату и время в формате ISO
  });
  const [error, setError] = useState('');
  const [templates, setTemplates] = useState([]); // Хранение доступных шаблонов
  const [createdCourses, setCreatedCourses] = useState([]); // Хранение созданных курсов

  // Загружаем доступные шаблоны при монтировании компонента
  useEffect(() => {
    const fetchTemplates = async () => {
      try {
        const response = await axios.get('/templates'); // Запрос для получения шаблонов
        setTemplates(response.data);
      } catch (err) {
        console.error('Ошибка при получении шаблонов:', err);
        setError('Не удалось загрузить шаблоны');
      }
    };

    fetchTemplates();

    // Получаем список созданных курсов
    const fetchCreatedCourses = async () => {
      try {
        const response = await axios.get(`/purchases/created-by/${user?.id}`); // Ваш endpoint для получения созданных курсов
        setCreatedCourses(response.data);
      } catch (err) {
        console.error('Ошибка при получении курсов:', err);
        setError('Не удалось загрузить курсы');
      }
    };

    fetchCreatedCourses();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    // Проверка типа файла
    if (file) {
      const fileType = file.type;
      if (fileType !== 'application/pdf' && !fileType.includes('msword') && !fileType.includes('officedocument')) {
        setError('Загрузите файл в формате PDF или Word');
        setForm((prev) => ({ ...prev, file: null })); // Очистить файл, если тип не поддерживается
      } else {
        setError('');
        setForm((prev) => ({ ...prev, file }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.file) {
      setError('Пожалуйста, загрузите файл');
      return;
    }
    if (!form.template_id) {
      setError('Пожалуйста, выберите шаблон');
      return;
    }
    const formData = new FormData();
    formData.append('title', form.title);
    formData.append('description', form.description);
    formData.append('price', form.price);
    formData.append('file', form.file);
    formData.append('template_id', form.template_id); // Передаем выбранный шаблон
    formData.append('created_at', form.created_at); // Передаем дату создания

    try {
      await axios.post('/materials', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Курс успешно загружен!');
      // После успешной загрузки обновим список курсов
      const response = await axios.get('/materials/created-by/me');
      setCreatedCourses(response.data);
      setForm({ title: '', description: '', price: '', file: null, template_id: '' });
    } catch (err) {
      alert('Ошибка при загрузке курса');
    }
  };

  // Обработчик удаления курса
  const handleDelete = async (courseId) => {
    if (!window.confirm('Удалить этот курс?')) return;
    try {
      await axios.delete(`/materials/${courseId}`);
      // Обновляем список курсов после удаления
      const response = await axios.get('/materials/created-by/me');
      setCreatedCourses(response.data);
      alert('Курс успешно удалён');
    } catch (err) {
      alert('Ошибка при удалении курса');
      console.error('Ошибка при удалении курса:', err);
    }
  };

  return (
    <div>
      <h2>Создание курса</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={form.title}
          onChange={handleChange}
          placeholder="Название курса"
          required
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Описание курса"
          required
        />
        <input
          type="number"
          name="price"
          value={form.price}
          onChange={handleChange}
          placeholder="Цена курса"
          required
        />

        {/* Выбор шаблона */}
        <select
          name="template_id"
          value={form.template_id}
          onChange={handleChange}
          required
        >
          <option value="">Выберите шаблон</option>
          {templates.map((template) => (
            <option key={template.id} value={template.id}>
              {template.name}
            </option>
          ))}
        </select>

        {/* Загрузка файла */}
        <input type="file" name="file" onChange={handleFileChange} required />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Загрузить курс</button>
      </form>

      {/* Список созданных курсов */}
      <h2>Ваши курсы</h2>
      {createdCourses.length === 0 ? (
        <p>Вы ещё не создали курсы.</p>
      ) : (
        <ul>
          {createdCourses.map((course) => (
            <li key={course.id}>
              <b>{course.title}</b> — {course.price}₽
              <button onClick={() => handleDelete(course.id)} style={{ marginLeft: '10px', color: 'red' }}>
                Удалить
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CreateCourse;
