import { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

export const useUser = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const loadUser = async () => {
      try {
        const res = await axios.get('/auth/me');
        setUser(res.data);
      } catch {
        setUser(null);
      }
    };
    loadUser();
  }, []);

  return user;
};
