import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function Dashboard() {
  const user = useUser(); // Получаем информацию о текущем пользователе
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/materials')
      .then((res) => {
        setMaterials(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleBuy = async (material_id) => {
    try {
      await axios.post('/purchases/buy', { course_material_id: material_id });
      alert('Курс куплен!');
    } catch (err) {
      alert('Вы уже приобрели этот курс или произошла ошибка.');
    }
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div>
      {/* Приветственное сообщение */}
      <h2>Добро пожаловать, {user?.username}!</h2>
      <p>Вы зашли в личный кабинет {user?.role_name}.</p>

      {/* Описание приложения */}
      <div className="app-description">
        <h3>О приложении</h3>
        <p>
          Это платформа для покупки и продажи учебных материалов, разработанных НГУ.
          Как покупатель, вы можете найти различные курсы по предметам, которые вам интересны.
          Как автор курса, вы можете загрузить свои материалы и начать продавать их.
          Управляйте своими курсами, отслеживайте статистику продаж и выводите средства.
        </p>
      </div>

      <h3>Доступные курсы:</h3>
      <div className="product-list">
        {materials.map((mat) => (
          <div key={mat.id} className="product-card">
            <h4>{mat.title}</h4>
            <p>{mat.description}</p>
            <div className="card-footer">
              <span>{mat.price}₽</span>
              <button onClick={() => handleBuy(mat.id)}>Купить</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
