import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useUser } from '../hooks/useUser';

function MethodologyList() {
  const user = useUser();
  const [methods, setMethods] = useState([]);

  useEffect(() => {
    if (!user) return;
    axios.get('/methodologies/my').then(res => setMethods(res.data));
  }, [user]);

  const handleDelete = async (id) => {
    if (!window.confirm('Удалить методологию?')) return;
    try {
      await axios.delete(`/methodologies/${id}`);
      setMethods(methods.filter(m => m.id !== id));
    } catch (err) {
      alert('Ошибка при удалении');
    }
  };

  if (!user || !['Учитель', 'Руководитель'].includes(user.role_name)) {
    return <p>Доступ запрещён</p>;
  }

  return (
    <div>
      <h2>Мои методологии</h2>
      {methods.length === 0 ? (
        <p>У вас пока нет методологий.</p>
      ) : (
        <ul>
          {methods.map(m => (
            <li key={m.id}>
              <b>{m.name}</b>: {m.content?.slice(0, 50)}...
              <button onClick={() => handleDelete(m.id)} style={{ marginLeft: '10px', color: 'red' }}>
                Удалить
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MethodologyList;
