import React from 'react';
import { useUser } from '../hooks/useUser';

function Profile() {
  const user = useUser();

  if (!user) return <p>Загрузка...</p>;

  return (
    <div>
      <h2>Личный кабинет: {user.username}</h2>
      {user.role_name === 'Руководитель' && (
        <div>
          <h3>Управление пользователями и контентом</h3>
          {/* Функции для управления контентом */}
        </div>
      )}
      {user.role_name === 'Амбассадор' && (
        <div>
          <h3>Управление своими курсами</h3>
          {/* Функции для управления курсами */}
        </div>
      )}
      {user.role_name === 'Гость' && (
        <div>
          <h3>История покупок</h3>
          {/* История покупок */}
        </div>
      )}
    </div>
  );
}

export default Profile;
